    },
    "system": "http://oval.mitre.org/XMLSchema/oval-definitions-5"
  },
  "id": "xccdf_mil.disa.stig_rule_SV-203652r958508_rule",
  "selected": "true",
  "role": "full",
  "severity": "medium"
}

