    "text": "CCI-004909",
    "system": "http://cyber.mil/cci"
  },
  "check": {
    "check-content-ref": {
      "href": "CertificateAuditTest.xml"
    },
    "system": "http://oval.mitre.org/XMLSchema/oval-definitions-5"
  },
  "id": "xccdf_mil.disa.stig_rule_SV-263659r982563_rule",
  "selected": "true",
  "role": "full",
  "severity": "medium"
}
